ItemChanger.CreateoreditSql:
declare @OrgUnitId int
declare @CPUid uniqueidentifier
declare @NName nvarchar(150) = '@NewName'

select @OrgUnitId = [OrgUnitId] from [dbo].[OrgUnit] where [Name] = '@OrgUnitName'      
if( @OrgUnitId is null )begin
    raiserror (N'Error - OrgUnit with Name = <%s> not found!', -- Message text.
        10, -- Severity,
        1, -- State,
        '@OrgUnitName' -- 1 argument.
    )
end
select @CPUid = [CPUid] from [dbo].[CP] where [Name] = '@Name'  

if @CPUid is not null begin --'Обновляем ТК'
    if @NName = null or @NName = '' set @NName = '@Name'
    exec [dbo].[CP_Update]
        default,
        @CPUid,
        '@Code',
        @NName,
        @UseExtractionPoint,
        @IsActive,
        @AllowNewEP,
        @EPIsRequired,
        '@Description'
    exec [dbo].[CPOrgUnit_Update]
        @CPUid,
        @OrgUnitId
end 
else begin --'Создаем ТК' 
    set @CPUid = newid();
    exec [dbo].[CP_Insert]
        @CPUid,
        '@Code',
        '@Name',
        @UseExtractionPoint,
        @IsActive,
        @AllowNewEP,
        @EPIsRequired,
        '@Description'  
    exec [dbo].[spc_CPOrgUnit_Insert]
        default,
        @CPUid,
        @OrgUnitId
end

ItemChanger.DeleteSql:
DECLARE @aeuid as uniqueidentifier = '@AEUid'

BEGIN TRANSACTION;
BEGIN TRY

	declare @AEBatchUids as table (AEBatchUid uniqueidentifier)
    insert into @AEBatchUids
    select [AEBatchUid]
    from [AEBatch] where AEUid = @AEUID

    PRINT N'[AEBatchRequest]:';
	DELETE FROM AEBatchRequest WHERE [AEBatchUid]  IN (
		SELECT [AEBatchUid] FROM @AEBatchUids
	);	


	PRINT N'[AEBatch]:';
	DELETE FROM [AEBatch] WHERE [AEBatchUid]  IN (
		SELECT [AEBatchUid] FROM @AEBatchUids
	);	

	PRINT N'[Period]:'
	DECLARE @ShelfLifePeriodId INT;
	SELECT 	@ShelfLifePeriodId = [ShelfLifePeriodId]			
	FROM [dbo].[AEBatch]
	WHERE [AEBatchUid] IN (
		SELECT [AEBatchUid] FROM @AEBatchUids
	);	
	IF (@ShelfLifePeriodId IS NOT NULL)
	BEGIN
		DECLARE @Len int, @PeriodTypeUid uniqueidentifier;
		SELECT @Len = [Len], @PeriodTypeUid = [PeriodTypeUid] FROM [Period] WHERE [PeriodId] = @ShelfLifePeriodId;
		EXEC [dbo].[Period_Delete]
			1,
			@ShelfLifePeriodId,
			@Len,
			@PeriodTypeUid;
	END;

	--PRINT N'[AEBatchLog]:';
	DELETE FROM [AEBatchLog] WHERE [AEBatchUid]  IN (
		SELECT [AEBatchUid] FROM @AEBatchUids
	);	

	--PRINT N'[AEBatchOp]:';
	DELETE FROM [AEBatchOp] WHERE [AEBatchUid]  IN (
		SELECT [AEBatchUid] FROM @AEBatchUids
	);

	--PRINT '[AEBatchRequest]:';
	DELETE FROM [AEBatchRequest] WHERE [AEUid]  IN (
		@aeuid
	);	

	--PRINT N'[AE]:';
	DELETE FROM [AE] WHERE [AEUid]  IN (
		@aeuid
	);	

	--PRINT N'[Period]:'
	SELECT 	@ShelfLifePeriodId = [ShelfLifePeriodId]			
	FROM [dbo].[AE]
	WHERE [AEUid] IN (
		@aeuid
	);	
	IF (@ShelfLifePeriodId IS NOT NULL)
	BEGIN
		SELECT @Len = [Len], @PeriodTypeUid = [PeriodTypeUid] FROM [Period] WHERE [PeriodId] = @ShelfLifePeriodId;
		EXEC [dbo].[Period_Delete]
			1,
			@ShelfLifePeriodId,
			@Len,
			@PeriodTypeUid;
	END;

END TRY
BEGIN CATCH
	PRINT ERROR_MESSAGE();
	IF @@TRANCOUNT > 0
	begin
		ROLLBACK TRANSACTION;
		PRINT N'Откат транзакции';
	end
END CATCH

IF @@TRANCOUNT > 0
	begin
		COMMIT TRANSACTION;
		--PRINT N'Применение транзакции';
	end

DECLARE @sampleLogId int = @SampleLogId
BEGIN TRANSACTION; BEGIN TRY DELETE FROM CheckProcSeriesTask where CheckProcSeriesTaskUid in (select CheckProcSeriesTaskUid FROM CheckProcTask WHERE SampleLogId = @sampleLogId) DELETE FROM CheckProcTask WHERE SampleLogId = @sampleLogId DELETE FROM dbo.SampleLogProof WHERE SampleLogId = @sampleLogId DELETE FROM dbo.SampleLogGroup WHERE SampleLogId = @sampleLogId DELETE FROM dbo.SampleLogModel WHERE SampleLogId = @sampleLogId DELETE FROM dbo.SampleLog WHERE SampleLogId = @sampleLogId END TRY BEGIN CATCH DECLARE @MSG NVARCHAR(max) = (SELECT ERROR_MESSAGE()) raiserror (-1,-1, @MSG ); IF @@TRANCOUNT > 0 ROLLBACK TRANSACTION;
END CATCH
IF @@TRANCOUNT > 0 COMMIT TRANSACTION;
